import React from 'react';
import { Link, useLocation} from "react-router-dom";

function WindowWithRegistrationAndLogin(props){

  const location = useLocation();
  const locationSignUp = location.pathname === "/sign-up";

  const [email, setEmail] = React.useState("");
  const [password, setpassword] = React.useState("");

  function handleEmailChange(event) {
    setEmail(event.target.value);
  }
  function handlePasswordChange(event) {
    setpassword(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    props.onSubmit(email, password);
  }

  return (
    <div className="welcome">
      <h1 className="welcome-title">{props.title}</h1>
      <form className="welcome-form" name={`${props.name}`} onSubmit={handleSubmit} >
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Email"
          className="welcome__input" 
          required  
          minLength="2" 
          maxLength="40"
          value={email} 
          onChange={handleEmailChange}/>
        <input
          type="password"
          id="password"
          name="password"
          placeholder="Пароль"
          className="welcome__input" 
          required  
          minLength="2" 
          maxLength="40"
          value={password} 
          onChange={handlePasswordChange}/>
        <button 
          type="submit" 
          className="welcome__button">
          {props.text}
        </button>
      </form>
      {locationSignUp && (
        <div className="welcome__sign-in">
          <p className="welcome__text">Уже зарегистрированы? </p>
          <Link to={"/sign-in"} className="welcome__login-link">Войти</Link>
        </div>
      )}
    </div>
  )
}

export default WindowWithRegistrationAndLogin;
