import React from "react";
import Card from "./Card.js";
import CurrentUserContext from "../contexts/CurrentUserContext.js";
import api from "../utils/Apijs"

function Main({onEditProfile, onEditAvatar, onAddPlace, onCardClick, cards}) {

  const currentUser = React.useContext(CurrentUserContext); // Подписываемся на контекст CurrentUserContext

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(like => like._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      });
}

  return (
    <main className="content">
    <section className="profile">
      <div className="profile__general-information">
      <img src={ currentUser?.avatar ? currentUser.avatar: 'https://my-engine.ru/modules/users/avatar.png'} alt="Аватар" className="profile__avatar"/>
        <button className="profile__avatar-hover" onClick={onEditAvatar}></button>
        <div className="profile__info">
          <div className="profile__name-string">
            <h1 className="profile__name">{currentUser.name}</h1> 
            <button type="button" className="profile__button-edit" onClick={onEditProfile}></button>
          </div>
          <p className="profile__description">{currentUser.description}</p>
        </div>
      </div>
        <button type="button" className="profile__button-add" onClick={onAddPlace}></button>
    </section>
    <section className="elements">
      <ul className="elements__list">
          {cards.map((card) => {
            return(
              <Card
                key={card._id}
                link={card.link}
                name={card.name}
                likes={card.likes.length}
                card={card}
                onCardClick={onCardClick}
            ></Card>
            )
          })
        }
      </ul>
    </section>
  </main>
  )
}

export default Main
